import './App.css';
import Nav from './components/Nav';
import {Cards} from './components/Cards';
import { useState,Switc } from 'react';
import {Login} from './components/Login.jsx';
import {Nav2} from './components/Nav2';
import {Reg} from './components/Reg';
import {AddStu} from './components/AddStu';
import {TestSys} from './components/TestSys';
import {Switch,Route} from 'react';

function App() {
  // const [reg,setReg]=useState({status:'card'})
  const [login,setLogin]=useState({status:true,data:{}})
  const [reg,setReg]=useState({status:'card'})

  if(login.status){
    return (<>
    <Nav setLogin={setLogin} setReg={setReg}></Nav>
    {reg.status=='card'?<Cards setReg={setReg}></Cards>:<Nav2/>}
    <Switch>
        <Route exact path="/Reg" Component={Reg}/>
        <Route exact path="/AddStu" Component={AddStu}/>
        <Route exact path="/TestSys" Component={TestSys}/>
      </Switch>
    </>)
  }
  else{
    return <>
       <Login setLogin={setLogin}> </Login>
    </>
  }

}
import { Form } from 'react-router-dom';

export default App;
